// Helper functions for name filtering and searching
function getNamesByElement(element: string) {
  return KOREAN_NAMES.filter(name => name.element === element);
}

function getNamesByGender(gender: string) {
  return KOREAN_NAMES.filter(name => name.gender === gender || name.gender === 'neutral');
}

function searchNamesByPronunciation(englishName: string) {
  const searchTerm = englishName.toLowerCase();
  return KOREAN_NAMES.filter(name => {
    const pronunciation = name.pronunciation.toLowerCase();
    const romanization = name.romanization.toLowerCase();
    return pronunciation.includes(searchTerm) ||
           romanization.includes(searchTerm) ||
           calculatePronunciationScore(englishName, name.pronunciation) > 2;
  }).sort((a, b) =>
    calculatePronunciationScore(englishName, b.pronunciation) -
    calculatePronunciationScore(englishName, a.pronunciation)
  );
}

